import React, { useState } from "react";
import "./Slider.css";

const Slider = () => {
  const slides = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,", // Ly nước 2
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRbjjVzyzlLJ3A8Pv-1r4CjBmcekhdG1zirAQ&s",
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRkF6GR350V3_q8xdEeoB2T5QbRp3r_8u9e4Q&s",
  ];
  

  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? slides.length - 1 : prevIndex - 1
    );
  };

  return (
    <div className="slider">
      <button className="prev" onClick={prevSlide}>
        &#10094;
      </button>
      <img src={slides[currentIndex]} alt={`Slide ${currentIndex + 1}`} />
      <button className="next" onClick={nextSlide}>
        &#10095;
      </button>
      <div className="dots">
        {slides.map((_, index) => (
          <span
            key={index}
            className={`dot ${index === currentIndex ? "active" : ""}`}
            onClick={() => setCurrentIndex(index)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default Slider;
